import { Injectable } from '@angular/core';
import { InMemoryDbService } from 'angular-in-memory-web-api';
import { Hero } from './hero';

@Injectable({
  providedIn: 'root',
})
export class InMemoryDataService implements InMemoryDbService {
  createDb() {
    const heroes = [
      { _id: 12, first_name: 'Dr. Nice1' },
      { _id: 13, first_name: 'Bombasto' },
      { _id: 14, first_name: 'Celeritas' },
      { _id: 15, first_name: 'Magneta' },
      { _id: 16, first_name: 'RubberMan' },
      { _id: 17, first_name: 'Dynama' },
      { _id: 18, first_name: 'Dr. IQ' },
      { _id: 19, first_name: 'Magma' },
      { _id: 20, first_name: 'Tornado' }
    ];
    return {heroes};
  }

  // Overrides the genId method to ensure that a hero always has an id.
  // If the heroes array is empty,
  // the method below returns the initial number (11).
  // if the heroes array is not empty, the method below returns the highest
  // hero id + 1.
  genId(heroes: Hero[]): number {
    return heroes.length > 0 ? Math.max(...heroes.map(hero => hero._id)) + 1 : 11;
  }
}
